import React, { useState, useEffect } from 'react';
import { authAPI, usersAPI, connectionAPI } from '../api';

const Register = ({ onRegister }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'student',
    faculty: 'FICT',
    class_id: ''
  });
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [initLoading, setInitLoading] = useState(true);
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    checkBackendAndFetchClasses();
  }, []);

  useEffect(() => {
    // Filter classes based on selected faculty
    if (formData.faculty && classes.length > 0) {
      const filtered = classes.filter(cls => cls.faculty === formData.faculty);
      console.log(`Filtered classes for ${formData.faculty}:`, filtered);
      setFilteredClasses(filtered);
    } else {
      setFilteredClasses(classes);
    }
  }, [formData.faculty, classes]);

  const checkBackendAndFetchClasses = async () => {
    try {
      setInitLoading(true);
      setDebugInfo('Checking backend connection...');
      
      console.log('ðŸ” Checking backend connection...');
      
      // Test backend connection
      try {
        const healthResponse = await connectionAPI.health();
        console.log('âœ… Backend health check:', healthResponse.data);
        setDebugInfo('Backend connected, fetching classes...');
      } catch (healthError) {
        console.error('âŒ Backend health check failed:', healthError);
        setDebugInfo('Backend connection failed');
        setError('Backend server is not running. Please start the backend server on port 5000.');
        return;
      }
      
      await fetchClasses();
      
    } catch (error) {
      console.error('Initialization error:', error);
      setDebugInfo('Initialization failed');
      setError('Failed to initialize application. Please try again.');
    } finally {
      setInitLoading(false);
    }
  };

  const fetchClasses = async () => {
    try {
      setDebugInfo('Fetching classes from server...');
      console.log('ðŸ”„ Fetching classes...');
      
      // Try multiple endpoints
      let classesData = [];
      
      try {
        // First try users/classes endpoint
        const response = await usersAPI.getClasses();
        console.log('âœ… Users/classes response:', response.data);
        
        if (response.data.success && response.data.classes) {
          classesData = response.data.classes;
          setDebugInfo(`Loaded ${classesData.length} classes from users/classes`);
        } else {
          throw new Error('No classes in users/classes response');
        }
      } catch (usersError) {
        console.log('âŒ users/classes failed, trying auth/init-data...');
        
        // Fallback to auth/init-data
        try {
          const initResponse = await authAPI.getInitData();
          console.log('âœ… Auth/init-data response:', initResponse.data);
          
          if (initResponse.data.success && initResponse.data.classes) {
            classesData = initResponse.data.classes;
            setDebugInfo(`Loaded ${classesData.length} classes from auth/init-data`);
          } else {
            throw new Error('No classes in auth/init-data response');
          }
        } catch (authError) {
          console.error('âŒ Both endpoints failed:', authError);
          throw new Error('Could not fetch classes from any endpoint');
        }
      }
      
      if (classesData.length > 0) {
        setClasses(classesData);
        console.log(`âœ… Successfully loaded ${classesData.length} classes`);
        setDebugInfo(`Successfully loaded ${classesData.length} classes`);
      } else {
        throw new Error('No classes data received');
      }
      
    } catch (error) {
      console.error('ðŸ’¥ Failed to fetch classes:', error);
      setDebugInfo('Failed to load classes');
      setError('Failed to load classes. Please check if the database has classes data.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validate class selection for students
    if (formData.role === 'student' && !formData.class_id) {
      setError('Please select a class for student registration');
      setLoading(false);
      return;
    }

    try {
      const response = await authAPI.register(formData);
      onRegister(response.data.user, response.data.token);
    } catch (error) {
      setError(error.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const retryConnection = () => {
    setError('');
    setDebugInfo('Retrying...');
    checkBackendAndFetchClasses();
  };

  const testEndpoints = async () => {
    console.log('ðŸ§ª Testing all endpoints...');
    
    try {
      // Test health endpoint
      const health = await connectionAPI.health();
      console.log('Health:', health.data);
      
      // Test classes endpoints
      try {
        const usersClasses = await usersAPI.getClasses();
        console.log('Users/Classes:', usersClasses.data);
      } catch (e) {
        console.error('Users/Classes failed:', e.message);
      }
      
      try {
        const authInit = await authAPI.getInitData();
        console.log('Auth/Init:', authInit.data);
      } catch (e) {
        console.error('Auth/Init failed:', e.message);
      }
      
    } catch (error) {
      console.error('Test failed:', error);
    }
  };

  if (initLoading) {
    return (
      <div className="card">
        <h2>Register - LUCT Reporting System</h2>
        <div className="loading">
          <p>Loading registration form...</p>
          <p>{debugInfo}</p>
          <button 
            onClick={testEndpoints}
            style={{ marginTop: '1rem' }}
            className="btn btn-sm btn-primary"
          >
            Test Endpoints
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <h2>Register - LUCT Reporting System</h2>
      
      {error && (
        <div className="alert alert-error">
          <strong>Error:</strong> {error}
          <div style={{ marginTop: '1rem' }}>
            <button 
              onClick={retryConnection}
              className="btn btn-sm btn-primary"
            >
              Retry Loading
            </button>
            <button 
              onClick={testEndpoints}
              style={{ marginLeft: '0.5rem' }}
              className="btn btn-sm btn-secondary"
            >
              Test Connection
            </button>
          </div>
        </div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Enter your full name"
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="form-control"
            required
            minLength="6"
            placeholder="Enter your password (min 6 characters)"
          />
        </div>

        <div className="form-group">
          <label>Role</label>
          <select
            name="role"
            value={formData.role}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="student">Student (Class Rep)</option>
            <option value="lecturer">Lecturer</option>
            <option value="prl">Principal Lecturer (PRL)</option>
            <option value="pl">Program Leader (PL)</option>
            <option value="fmg">Faculty Management (FMG)</option>
          </select>
        </div>

        <div className="form-group">
          <label>Faculty</label>
          <select
            name="faculty"
            value={formData.faculty}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="FICT">FICT - Faculty of Information Communication Technology</option>
            <option value="FBMG">FBMG - Faculty of Business Management</option>
            <option value="FABE">FABE - Faculty of Architecture and Built Environment</option>
          </select>
        </div>

        {(formData.role === 'student' || formData.role === 'lecturer') && (
          <div className="form-group">
            <label>
              {formData.role === 'student' ? 'Select Your Class' : 'Select Assigned Class (Optional)'}
            </label>
            <select
              name="class_id"
              value={formData.class_id}
              onChange={handleChange}
              className="form-control"
              required={formData.role === 'student'}
            >
              <option value="">Select Class</option>
              {filteredClasses.map(cls => (
                <option key={cls.id} value={cls.id}>
                  {cls.name} ({cls.faculty})
                </option>
              ))}
            </select>
            <small style={{ color: '#cccccc', marginTop: '0.5rem', display: 'block' }}>
              {filteredClasses.length} classes available for {formData.faculty}
              {classes.length > 0 && ` (Total: ${classes.length} classes)`}
            </small>
          </div>
        )}

        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={loading || (formData.role === 'student' && filteredClasses.length === 0)}
        >
          {loading ? 'Registering...' : 'Register'}
        </button>
      </form>

      <div style={{ marginTop: '1rem', textAlign: 'center' }}>
        <p>Already have an account? <a href="/login" style={{ color: '#cccccc' }}>Login here</a></p>
      </div>

      {/* Debug info */}
      <div style={{ marginTop: '2rem', padding: '1rem', backgroundColor: '#2c2c2c', borderRadius: '4px' }}>
        <h4>Debug Information:</h4>
        <p><strong>Status:</strong> {debugInfo}</p>
        <p><strong>Backend:</strong> Connected</p>
        <p><strong>Classes Loaded:</strong> {classes.length}</p>
        <p><strong>Filtered Classes:</strong> {filteredClasses.length}</p>
        <button 
          onClick={testEndpoints}
          className="btn btn-sm btn-secondary"
        >
          Test All Endpoints
        </button>
      </div>
    </div>
  );
};

export default Register;
