import React, { useState, useEffect } from 'react';
import { complaintsAPI, usersAPI } from '../api';
import { useNavigate } from 'react-router-dom';

const ComplaintForm = ({ user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    target_user_id: '',
    complaint_type: 'lecturer'
  });
  const [lecturers, setLecturers] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchLecturers();
  }, []);

  const fetchLecturers = async () => {
    try {
      const response = await usersAPI.getLecturers();
      // Filter out current user to prevent self-complaint
      const filteredLecturers = response.data.lecturers.filter(
        lecturer => lecturer.id !== user.id
      );
      setLecturers(filteredLecturers);
    } catch (error) {
      console.error('Failed to fetch lecturers:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const getComplaintTypeDescription = (type) => {
    const descriptions = {
      lecturer: 'Complaint about a lecturer',
      prl: 'Complaint about a Principal Lecturer (PRL)',
      pl: 'Complaint about a Program Leader (PL)',
      fmg: 'Complaint about Faculty Management (FMG)'
    };
    return descriptions[type] || 'Complaint';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      await complaintsAPI.create(formData);
      setSuccess('Complaint filed successfully! The recipient will be notified.');
      setTimeout(() => {
        navigate('/complaints');
      }, 2000);
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to file complaint. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Filter lecturers based on complaint type
  const filteredLecturers = lecturers.filter(lecturer => {
    if (formData.complaint_type === 'prl') {
      return lecturer.role === 'prl';
    } else if (formData.complaint_type === 'pl') {
      return lecturer.role === 'pl';
    } else if (formData.complaint_type === 'fmg') {
      return lecturer.role === 'fmg';
    }
    return lecturer.role === 'lecturer';
  });

  return (
    <div className="card">
      <h2>File a Complaint</h2>
      {error && <div className="alert alert-error">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Complaint Type</label>
          <select
            name="complaint_type"
            value={formData.complaint_type}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="lecturer">Complaint about Lecturer</option>
            <option value="prl">Complaint about Principal Lecturer (PRL)</option>
            <option value="pl">Complaint about Program Leader (PL)</option>
            <option value="fmg">Complaint about Faculty Management (FMG)</option>
          </select>
          <small style={{ color: '#cccccc', marginTop: '0.5rem', display: 'block' }}>
            {getComplaintTypeDescription(formData.complaint_type)}
          </small>
        </div>

        <div className="form-group">
          <label>Complaint Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Brief title for your complaint"
          />
        </div>

        <div className="form-group">
          <label>Complaint Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            className="form-control"
            required
            rows="6"
            placeholder="Please provide detailed information about your complaint..."
          />
        </div>

        <div className="form-group">
          <label>Complaint Against</label>
          <select
            name="target_user_id"
            value={formData.target_user_id}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="">Select {formData.complaint_type.toUpperCase()}</option>
            {filteredLecturers.map(lecturer => (
              <option key={lecturer.id} value={lecturer.id}>
                {lecturer.name} ({lecturer.role.toUpperCase()} - {lecturer.faculty})
              </option>
            ))}
          </select>
          {filteredLecturers.length === 0 && (
            <small style={{ color: '#ffc107', marginTop: '0.5rem', display: 'block' }}>
              No {formData.complaint_type.toUpperCase()} available for complaint.
            </small>
          )}
        </div>

        <div className="action-buttons">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading || filteredLecturers.length === 0}
          >
            {loading ? 'Filing Complaint...' : 'File Complaint'}
          </button>
          <button 
            type="button" 
            className="btn btn-danger"
            onClick={() => navigate('/dashboard')}
          >
            Cancel
          </button>
        </div>
      </form>

      <div style={{ marginTop: '2rem', padding: '1rem', backgroundColor: '#2c2c2c', borderRadius: '4px' }}>
        <h4>Complaint Escalation Process:</h4>
        <ul style={{ color: '#cccccc', marginLeft: '1.5rem', marginTop: '0.5rem' }}>
          <li>Student → Lecturer: Complaint goes to the lecturer</li>
          <li>Lecturer → PRL: Complaint about lecturer goes to PRL</li>
          <li>PRL → PL: Complaint about PRL goes to Program Leader</li>
          <li>PL → FMG: Complaint about PL goes to Faculty Management</li>
          <li>No one can file complaints against themselves</li>
        </ul>
      </div>
    </div>
  );
};

export default ComplaintForm;
