import React, { useState, useEffect } from 'react';
import { reportsAPI, complaintsAPI } from '../api';

const Dashboard = ({ user }) => {
  const [stats, setStats] = useState({
    myReports: 0,
    unsignedReports: 0,
    myComplaints: 0,
    complaintsForMe: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      const [reportsRes, unsignedRes, myComplaintsRes, forMeRes] = await Promise.all([
        reportsAPI.getMyReports(),
        reportsAPI.getUnsignedReports(),
        complaintsAPI.getMyComplaints(),
        complaintsAPI.getComplaintsForMe()
      ]);

      setStats({
        myReports: reportsRes.data.reports?.length || 0,
        unsignedReports: unsignedRes.data.reports?.length || 0,
        myComplaints: myComplaintsRes.data.complaints?.length || 0,
        complaintsForMe: forMeRes.data.complaints?.length || 0
      });
    } catch (error) {
      console.error('Failed to fetch dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div>
      <div className="welcome-message">
        <h2>Welcome back, {user.name}!</h2>
        <p>LUCT Reporting System - {user.faculty} Faculty</p>
        <p>Role: {user.role.toUpperCase()}</p>
        {user.class_name && <p>Class: {user.class_name}</p>}
      </div>

      <div className="dashboard-grid">
        {/* Lecturer/PRL/PL/FMG Cards */}
        {['lecturer', 'prl', 'pl', 'fmg'].includes(user.role) && (
          <>
            <div className="dashboard-card">
              <h3>My Reports</h3>
              <p>{stats.myReports} report(s) submitted</p>
              <div className="action-buttons">
                <a href="/reports/new" className="btn btn-primary">Create New Report</a>
                <a href="/reports" className="btn btn-success">View All Reports</a>
              </div>
            </div>

            <div className="dashboard-card">
              <h3>Unsigned Reports</h3>
              <p>{stats.unsignedReports} report(s) waiting for student signature</p>
              <a href="/reports" className="btn btn-primary">Review Reports</a>
            </div>
          </>
        )}

        {/* Student Cards */}
        {user.role === 'student' && (
          <>
            <div className="dashboard-card">
              <h3>Reports to Sign</h3>
              <p>{stats.unsignedReports} report(s) waiting for your signature</p>
              <a href="/reports" className="btn btn-primary">Sign Reports</a>
            </div>
          </>
        )}

        {/* Complaints for all roles */}
        <div className="dashboard-card">
          <h3>My Complaints</h3>
          <p>{stats.myComplaints} complaint(s) filed by you</p>
          <div className="action-buttons">
            <a href="/complaints/new" className="btn btn-primary">File New Complaint</a>
            <a href="/complaints" className="btn btn-success">View My Complaints</a>
          </div>
        </div>

        <div className="dashboard-card">
          <h3>Complaints for Me</h3>
          <p>{stats.complaintsForMe} complaint(s) require your response</p>
          <a href="/complaints" className="btn btn-primary">Respond to Complaints</a>
        </div>

        {/* PL Management */}
        {user.role === 'pl' && (
          <div className="dashboard-card">
            <h3>User Management</h3>
            <p>Manage classes, courses, and assignments</p>
            <a href="/manage-users" className="btn btn-primary">Manage Users</a>
          </div>
        )}

        {/* System Overview */}
        <div className="dashboard-card">
          <h3>Quick Actions</h3>
          <p>Access frequently used features</p>
          <div className="action-buttons" style={{ flexDirection: 'column', gap: '0.5rem' }}>
            <a href="/reports" className="btn btn-sm btn-primary">All Reports</a>
            <a href="/complaints" className="btn btn-sm btn-success">All Complaints</a>
            {user.role === 'pl' && (
              <a href="/manage-users" className="btn btn-sm btn-primary">User Management</a>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
