import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import './Monitoring.css';

const Monitoring = () => {
  const { user } = useAuth();
  const [attendanceData, setAttendanceData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    faculty: '',
    course: '',
    lecturer: '',
    week: ''
  });
  const [loading, setLoading] = useState(true);

  // Mock data - replace with actual API calls
  useEffect(() => {
    const mockData = [
      {
        id: 1,
        faculty: 'ICT',
        className: 'DIT-1A',
        courseName: 'Web Development',
        courseCode: 'DIWA2110',
        lecturer: 'Dr. Smith',
        week: 'Week 6',
        date: '2024-03-15',
        present: 25,
        registered: 30,
        attendanceRate: '83%',
        venue: 'Lab 101',
        scheduledTime: '09:00-11:00'
      },
      {
        id: 2,
        faculty: 'ICT',
        className: 'BIT-2B',
        courseName: 'Database Systems',
        courseCode: 'DIDB2110',
        lecturer: 'Dr. Johnson',
        week: 'Week 6',
        date: '2024-03-16',
        present: 18,
        registered: 22,
        attendanceRate: '82%',
        venue: 'Room 205',
        scheduledTime: '14:00-16:00'
      }
    ];

    setAttendanceData(mockData);
    setFilteredData(mockData);
    setLoading(false);
  }, []);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const applyFilters = () => {
    let filtered = attendanceData;

    if (filters.faculty) {
      filtered = filtered.filter(item => 
        item.faculty.toLowerCase().includes(filters.faculty.toLowerCase())
      );
    }

    if (filters.course) {
      filtered = filtered.filter(item => 
        item.courseName.toLowerCase().includes(filters.course.toLowerCase()) ||
        item.courseCode.toLowerCase().includes(filters.course.toLowerCase())
      );
    }

    if (filters.lecturer) {
      filtered = filtered.filter(item => 
        item.lecturer.toLowerCase().includes(filters.lecturer.toLowerCase())
      );
    }

    if (filters.week) {
      filtered = filtered.filter(item => 
        item.week.toLowerCase().includes(filters.week.toLowerCase())
      );
    }

    setFilteredData(filtered);
  };

  const resetFilters = () => {
    setFilters({
      faculty: '',
      course: '',
      lecturer: '',
      week: ''
    });
    setFilteredData(attendanceData);
  };

  if (loading) {
    return <div className="monitoring-container">Loading monitoring data...</div>;
  }

  return (
    <div className="monitoring-container">
      <div className="monitoring-header">
        <h1>Class Monitoring</h1>
        <p>Monitor attendance and class activities across all faculties</p>
      </div>

      {/* Filters */}
      <div className="monitoring-filters">
        <div className="filter-row">
          <div className="filter-group">
            <label>Faculty</label>
            <input
              type="text"
              name="faculty"
              value={filters.faculty}
              onChange={handleFilterChange}
              placeholder="Filter by faculty"
            />
          </div>
          <div className="filter-group">
            <label>Course</label>
            <input
              type="text"
              name="course"
              value={filters.course}
              onChange={handleFilterChange}
              placeholder="Filter by course name/code"
            />
          </div>
          <div className="filter-group">
            <label>Lecturer</label>
            <input
              type="text"
              name="lecturer"
              value={filters.lecturer}
              onChange={handleFilterChange}
              placeholder="Filter by lecturer"
            />
          </div>
          <div className="filter-group">
            <label>Week</label>
            <input
              type="text"
              name="week"
              value={filters.week}
              onChange={handleFilterChange}
              placeholder="Filter by week"
            />
          </div>
        </div>
        <div className="filter-actions">
          <button onClick={applyFilters} className="btn-primary">
            Apply Filters
          </button>
          <button onClick={resetFilters} className="btn-secondary">
            Reset
          </button>
        </div>
      </div>

      {/* Statistics */}
      <div className="monitoring-stats">
        <div className="stat-card">
          <h3>Total Classes</h3>
          <p>{filteredData.length}</p>
        </div>
        <div className="stat-card">
          <h3>Average Attendance</h3>
          <p>
            {filteredData.length > 0 
              ? Math.round(filteredData.reduce((acc, curr) => acc + parseInt(curr.attendanceRate), 0) / filteredData.length) + '%'
              : '0%'
            }
          </p>
        </div>
        <div className="stat-card">
          <h3>Total Students</h3>
          <p>
            {filteredData.reduce((acc, curr) => acc + curr.registered, 0)}
          </p>
        </div>
      </div>

      {/* Attendance Table */}
      <div className="attendance-table">
        <table>
          <thead>
            <tr>
              <th>Faculty</th>
              <th>Class</th>
              <th>Course</th>
              <th>Lecturer</th>
              <th>Week</th>
              <th>Date</th>
              <th>Attendance</th>
              <th>Venue</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((record) => (
              <tr key={record.id}>
                <td>{record.faculty}</td>
                <td>{record.className}</td>
                <td>
                  <div>{record.courseName}</div>
                  <small>{record.courseCode}</small>
                </td>
                <td>{record.lecturer}</td>
                <td>{record.week}</td>
                <td>{record.date}</td>
                <td>
                  <div className="attendance-rate">
                    <span className={`rate ${parseInt(record.attendanceRate) < 70 ? 'low' : ''}`}>
                      {record.attendanceRate}
                    </span>
                    <small>({record.present}/{record.registered})</small>
                  </div>
                </td>
                <td>{record.venue}</td>
                <td>{record.scheduledTime}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {filteredData.length === 0 && (
          <div className="no-data">
            No monitoring data found matching your filters.
          </div>
        )}
      </div>
    </div>
  );
};

export default Monitoring;
