import React, { useState, useEffect } from 'react';
import { reportsAPI, complaintsAPI, usersAPI } from '../api';

const Dashboard = ({ user }) => {
  const [stats, setStats] = useState({
    myReports: 0,
    unsignedReports: 0,
    myComplaints: 0,
    complaintsForMe: 0,
    totalStudents: 0,
    totalLecturers: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError('');

      // Create an array of promises for all API calls
      const promises = [];

      // Basic stats for all users
      promises.push(
        reportsAPI.getMyReports().catch(error => {
          console.error('Failed to fetch my reports:', error);
          return { data: { reports: [] } };
        }),
        reportsAPI.getUnsignedReports().catch(error => {
          console.error('Failed to fetch unsigned reports:', error);
          return { data: { reports: [] } };
        }),
        complaintsAPI.getMyComplaints().catch(error => {
          console.error('Failed to fetch my complaints:', error);
          return { data: { complaints: [] } };
        }),
        complaintsAPI.getComplaintsForMe().catch(error => {
          console.error('Failed to fetch complaints for me:', error);
          return { data: { complaints: [] } };
        })
      );

      // Additional data for PL and FMG
      if (user.role === 'pl' || user.role === 'fmg') {
        promises.push(
          usersAPI.getStudents().catch(error => {
            console.error('Failed to fetch students:', error);
            return { data: { students: [] } };
          }),
          usersAPI.getLecturers().catch(error => {
            console.error('Failed to fetch lecturers:', error);
            return { data: { lecturers: [] } };
          })
        );
      }

      const results = await Promise.all(promises);

      // Extract data from results
      const [
        myReportsRes, 
        unsignedReportsRes, 
        myComplaintsRes, 
        complaintsForMeRes,
        studentsRes,
        lecturersRes
      ] = results;

      // Set basic stats
      const newStats = {
        myReports: myReportsRes.data.reports?.length || 0,
        unsignedReports: unsignedReportsRes.data.reports?.length || 0,
        myComplaints: myComplaintsRes.data.complaints?.length || 0,
        complaintsForMe: complaintsForMeRes.data.complaints?.length || 0,
        totalStudents: studentsRes?.data?.students?.length || 0,
        totalLecturers: lecturersRes?.data?.lecturers?.length || 0
      };

      setStats(newStats);

    } catch (error) {
      console.error('Failed to fetch dashboard data:', error);
      setError('Failed to load dashboard data. Some information may be unavailable.');
    } finally {
      setLoading(false);
    }
  };

  const getWelcomeMessage = () => {
    const hours = new Date().getHours();
    let greeting = 'Welcome';
    
    if (hours < 12) greeting = 'Good morning';
    else if (hours < 18) greeting = 'Good afternoon';
    else greeting = 'Good evening';

    return `${greeting}, ${user.name}!`;
  };

  if (loading) {
    return (
      <div className="card">
        <h2>Loading Dashboard...</h2>
        <div className="loading">
          <p>Fetching your data...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="welcome-message">
        <h2>{getWelcomeMessage()}</h2>
        <p><strong>LUCT Reporting System</strong> - {user.faculty} Faculty</p>
        <p><strong>Role:</strong> {user.role.toUpperCase()}</p>
        {user.class_name && <p><strong>Class:</strong> {user.class_name}</p>}
        {error && (
          <div className="alert alert-error" style={{ marginTop: '1rem' }}>
            {error}
            <button 
              onClick={fetchDashboardData} 
              style={{ marginLeft: '1rem' }}
              className="btn btn-sm btn-primary"
            >
              Retry
            </button>
          </div>
        )}
      </div>

      <div className="dashboard-grid">
        {/* Reports Section */}
        {['lecturer', 'prl', 'pl', 'fmg'].includes(user.role) && (
          <>
            <div className="dashboard-card">
              <h3>üìä My Reports</h3>
              <p className="stat-number">{stats.myReports}</p>
              <p>report(s) submitted</p>
              <div className="action-buttons">
                <a href="/reports/new" className="btn btn-primary">Create New Report</a>
                <a href="/reports" className="btn btn-success">View All Reports</a>
              </div>
            </div>

            <div className="dashboard-card">
              <h3>üìù Pending Signatures</h3>
              <p className="stat-number">{stats.unsignedReports}</p>
              <p>report(s) waiting for student signature</p>
              <a href="/reports" className="btn btn-primary">Review Reports</a>
            </div>
          </>
        )}

        {/* Student Section */}
        {user.role === 'student' && (
          <div className="dashboard-card">
            <h3>‚úçÔ∏è Reports to Sign</h3>
            <p className="stat-number">{stats.unsignedReports}</p>
            <p>report(s) waiting for your signature</p>
            <a href="/reports" className="btn btn-primary">Sign Reports</a>
          </div>
        )}

        {/* Complaints Section */}
        <div className="dashboard-card">
          <h3>üì® My Complaints</h3>
          <p className="stat-number">{stats.myComplaints}</p>
          <p>complaint(s) filed by you</p>
          <div className="action-buttons">
            <a href="/complaints/new" className="btn btn-primary">File New Complaint</a>
            <a href="/complaints" className="btn btn-success">View My Complaints</a>
          </div>
        </div>

        <div className="dashboard-card">
          <h3>üì© Complaints for Me</h3>
          <p className="stat-number">{stats.complaintsForMe}</p>
          <p>complaint(s) require your response</p>
          <a href="/complaints" className="btn btn-primary">Respond to Complaints</a>
        </div>

        {/* Management Section for PL/FMG */}
        {(user.role === 'pl' || user.role === 'fmg') && (
          <>
            <div className="dashboard-card">
              <h3>üë• User Management</h3>
              <p><strong>Students:</strong> {stats.totalStudents}</p>
              <p><strong>Lecturers:</strong> {stats.totalLecturers}</p>
              <a href="/manage-users" className="btn btn-primary">Manage Users</a>
            </div>
          </>
        )}

        {/* Quick Actions */}
        <div className="dashboard-card">
          <h3>‚ö° Quick Actions</h3>
          <p>Access frequently used features</p>
          <div className="action-buttons" style={{ flexDirection: 'column', gap: '0.5rem' }}>
            <a href="/reports" className="btn btn-sm btn-primary">All Reports</a>
            <a href="/complaints" className="btn btn-sm btn-success">All Complaints</a>
            {user.role === 'pl' && (
              <a href="/manage-users" className="btn btn-sm btn-primary">User Management</a>
            )}
            <button 
              onClick={fetchDashboardData} 
              className="btn btn-sm btn-secondary"
            >
              Refresh Data
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
