// frontend/src/services/api.js
import axios from 'axios';

// Base URL from environment or fallback
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
console.log('ðŸ”— API Base URL:', API_BASE_URL);

// Axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: { 'Content-Type': 'application/json' },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) config.headers.Authorization = `Bearer ${token}`;
    console.log(`ðŸ”„ API Request: ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('âŒ Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Success: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    const details = {
      url: error.config?.url,
      method: error.config?.method?.toUpperCase(),
      status: error.response?.status,
      statusText: error.response?.statusText,
      message: error.message,
      code: error.code,
    };
    console.error('âŒ API Error:', details);

    // User-friendly messages
    if (error.code === 'ECONNREFUSED') {
      error.userMessage = 'Cannot connect to backend. Make sure the server is running.';
    } else if (error.response?.status === 500) {
      error.userMessage = 'Server error occurred. Please try again later.';
    } else if (error.response?.data?.error) {
      error.userMessage = error.response.data.error;
    } else if (error.response?.data?.message) {
      error.userMessage = error.response.data.message;
    } else {
      error.userMessage = error.message || 'An unexpected error occurred.';
    }

    // Handle 401 Unauthorized
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

// Retry wrapper
const createApiWithRetry = (apiCall, maxRetries = 2) => {
  return async (...args) => {
    let lastError;
    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        return await apiCall(...args);
      } catch (error) {
        lastError = error;
        console.log(`ðŸ”„ Retry ${attempt}/${maxRetries} for ${args[0]}`);
        if (attempt === maxRetries) break;
        await new Promise((resolve) => setTimeout(resolve, 1000 * attempt));
      }
    }
    throw lastError;
  };
};

// ===== Auth API =====
export const authAPI = {
  login: createApiWithRetry((data) => api.post('/auth/login', data)),
  register: createApiWithRetry((data) => api.post('/auth/register', data)),
  getInitData: createApiWithRetry(() => api.get('/auth/init-data')),
};

// ===== Reports API =====
export const reportsAPI = {
  create: createApiWithRetry((data) => api.post('/reports', data)),
  getMyReports: createApiWithRetry(() => api.get('/reports/my-reports')),
  getUnsignedReports: createApiWithRetry(() => api.get('/reports/unsigned')),
  signReport: createApiWithRetry((id) => api.put(`/reports/${id}/sign`)),
  getAll: createApiWithRetry(() => api.get('/reports')),
};

// ===== Users API =====
export const usersAPI = {
  getLecturers: createApiWithRetry(() => api.get('/users/lecturers')),
  getStudents: createApiWithRetry(() => api.get('/users/students')),
  getClasses: createApiWithRetry(() => api.get('/users/classes')),
  getCourses: createApiWithRetry(() => api.get('/users/courses')),
  assignClass: createApiWithRetry((data) => api.post('/users/assign-class', data)),
};

// ===== Complaints API =====
export const complaintsAPI = {
  create: createApiWithRetry((data) => api.post('/complaints', data)),
  getMyComplaints: createApiWithRetry(() => api.get('/complaints/my-complaints')),
  getComplaintsForMe: createApiWithRetry(() => api.get('/complaints/for-me')),
  respond: createApiWithRetry((id, response) => api.put(`/complaints/${id}/respond`, { response })),
  getAll: createApiWithRetry(() => api.get('/complaints')),
};

// ===== Connection / Health API =====
export const connectionAPI = {
  health: () => api.get('/health'),
  testAllEndpoints: async () => {
    const results = {};
    try {
      results.health = await api.get('/health');
      results.classes = await api.get('/users/classes').catch((e) => ({ error: e.message }));
      results.courses = await api.get('/users/courses').catch((e) => ({ error: e.message }));
      return results;
    } catch (error) {
      console.error('Comprehensive connection test failed:', error);
      return { error: error.message };
    }
  },
};

export default api;
