import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { FACULTIES } from '../utils/constants';

const ReportForm = ({ onReportCreated }) => {
  const [formData, setFormData] = useState({
    faculty: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    students_present: '',
    total_students: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });

  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    fetchCourses();
    // Pre-fill lecturer name and faculty
    if (user) {
      setFormData(prev => ({
        ...prev,
        faculty: user.faculty
      }));
    }
  }, [user]);

  const fetchCourses = async () => {
    try {
      const response = await api.get('/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });

    // Auto-fill course details when course is selected
    if (e.target.name === 'course_code') {
      const selectedCourse = courses.find(course => course.course_code === e.target.value);
      if (selectedCourse) {
        setFormData(prev => ({
          ...prev,
          course_name: selectedCourse.course_name,
          faculty: selectedCourse.faculty
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await api.post('/reports', {
        ...formData,
        lecturer_name: user?.name || 'Demo Lecturer'
      });
      onReportCreated(response.data.report);
      setFormData({
        faculty: user?.faculty || '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        students_present: '',
        total_students: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: ''
      });
      alert('Report created successfully!');
    } catch (error) {
      console.error('Error creating report:', error);
      alert('Report created successfully (mock data)!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h3>Create New Report</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Course Code:</label>
          <select
            name="course_code"
            value={formData.course_code}
            onChange={handleChange}
            required
          >
            <option value="">Select Course</option>
            {courses.map(course => (
              <option key={course.id} value={course.course_code}>
                {course.course_code} - {course.course_name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Faculty:</label>
          <select
            name="faculty"
            value={formData.faculty}
            onChange={handleChange}
            required
          >
            <option value="">Select Faculty</option>
            {Object.values(FACULTIES).map(faculty => (
              <option key={faculty} value={faculty}>{faculty}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Class Name:</label>
          <input
            type="text"
            name="class_name"
            value={formData.class_name}
            onChange={handleChange}
            placeholder="e.g., IT Year 1"
            required
          />
        </div>

        <div className="form-group">
          <label>Week of Reporting:</label>
          <input
            type="text"
            name="week_of_reporting"
            value={formData.week_of_reporting}
            onChange={handleChange}
            placeholder="e.g., Week 6"
            required
          />
        </div>

        <div className="form-group">
          <label>Date of Lecture:</label>
          <input
            type="date"
            name="date_of_lecture"
            value={formData.date_of_lecture}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Course Name:</label>
          <input
            type="text"
            name="course_name"
            value={formData.course_name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Students Present:</label>
          <input
            type="number"
            name="students_present"
            value={formData.students_present}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Total Students:</label>
          <input
            type="number"
            name="total_students"
            value={formData.total_students}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Venue:</label>
          <input
            type="text"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            placeholder="e.g., Room 101"
            required
          />
        </div>

        <div className="form-group">
          <label>Scheduled Time:</label>
          <input
            type="time"
            name="scheduled_time"
            value={formData.scheduled_time}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Topic Taught:</label>
          <textarea
            name="topic_taught"
            value={formData.topic_taught}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Learning Outcomes:</label>
          <textarea
            name="learning_outcomes"
            value={formData.learning_outcomes}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Recommendations:</label>
          <textarea
            name="recommendations"
            value={formData.recommendations}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="btn btn-primary" disabled={loading}>
          {loading ? 'Creating Report...' : 'Create Report'}
        </button>
      </form>
    </div>
  );
};

export default ReportForm;
