import React, { useState, useEffect } from 'react';
import { authAPI, usersAPI, connectionAPI } from '../api';

const Register = ({ onRegister }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'student',
    faculty: 'FICT',
    class_id: ''
  });
  const [classes, setClasses] = useState([]);
  const [filteredClasses, setFilteredClasses] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [initLoading, setInitLoading] = useState(true);
  const [backendStatus, setBackendStatus] = useState('checking');

  useEffect(() => {
    checkBackendAndFetchClasses();
  }, []);

  useEffect(() => {
    // Filter classes based on selected faculty
    if (formData.faculty && classes.length > 0) {
      const filtered = classes.filter(cls => cls.faculty === formData.faculty);
      setFilteredClasses(filtered);
    } else {
      setFilteredClasses(classes);
    }
  }, [formData.faculty, classes]);

  const checkBackendAndFetchClasses = async () => {
    try {
      setInitLoading(true);
      setBackendStatus('checking');
      
      console.log('🔍 Checking backend connection...');
      
      // Use connectionAPI.health() instead of testConnection
      const healthResponse = await connectionAPI.health();
      const isBackendRunning = healthResponse && healthResponse.status === 200;
      
      if (!isBackendRunning) {
        setBackendStatus('down');
        setError('Backend server is not running. Please make sure the backend is started on port 5000.');
        return;
      }
      
      setBackendStatus('up');
      await fetchClasses();
      
    } catch (error) {
      console.error('Initialization error:', error);
      setBackendStatus('error');
      setError('Failed to connect to backend server. Please ensure the backend is running on port 5000.');
    } finally {
      setInitLoading(false);
    }
  };

  const fetchClasses = async () => {
    try {
      console.log('🔄 Fetching classes...');
      
      // Try users/classes endpoint first
      const response = await usersAPI.getClasses();
      
      if (response.data && response.data.classes) {
        setClasses(response.data.classes);
        console.log(`✅ Loaded ${response.data.classes.length} classes`);
      } else {
        throw new Error('No classes data in response');
      }
      
    } catch (error) {
      console.error('Failed to fetch classes:', error);
      setError('Failed to load classes. Please try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (backendStatus !== 'up') {
      setError('Backend server is not available. Please start the backend server.');
      return;
    }
    
    setLoading(true);
    setError('');

    // Validate class selection for students
    if (formData.role === 'student' && !formData.class_id) {
      setError('Please select a class for student registration');
      setLoading(false);
      return;
    }

    try {
      const response = await authAPI.register(formData);
      onRegister(response.data.user, response.data.token);
    } catch (error) {
      setError(error.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const retryConnection = () => {
    setError('');
    checkBackendAndFetchClasses();
  };

  if (initLoading) {
    return (
      <div className="card">
        <h2>Register - LUCT Reporting System</h2>
        <div className="loading">
          <p>Checking backend connection...</p>
          <p>Status: {backendStatus}</p>
        </div>
      </div>
    );
  }

  if (backendStatus !== 'up') {
    return (
      <div className="card">
        <h2>Register - LUCT Reporting System</h2>
        <div className="alert alert-error">
          <h3>Backend Server Not Available</h3>
          <p>Please start the backend server to continue:</p>
          <ol style={{ textAlign: 'left', margin: '1rem 0' }}>
            <li>Open a new terminal/command prompt</li>
            <li>Navigate to the backend folder: <code>cd backend</code></li>
            <li>Start the server: <code>npm start</code></li>
            <li>Wait for "Server running on port 5000" message</li>
            <li>Refresh this page</li>
          </ol>
          <button onClick={retryConnection} className="btn btn-primary">
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <h2>Register - LUCT Reporting System</h2>
      
      {error && (
        <div className="alert alert-error">
          {error}
          <button 
            onClick={retryConnection}
            style={{ marginLeft: '1rem' }}
            className="btn btn-sm btn-primary"
          >
            Retry
          </button>
        </div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Enter your full name"
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="form-control"
            required
            minLength="6"
            placeholder="Enter your password (min 6 characters)"
          />
        </div>

        <div className="form-group">
          <label>Role</label>
          <select
            name="role"
            value={formData.role}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="student">Student (Class Rep)</option>
            <option value="lecturer">Lecturer</option>
            <option value="prl">Principal Lecturer (PRL)</option>
            <option value="pl">Program Leader (PL)</option>
            <option value="fmg">Faculty Management (FMG)</option>
          </select>
        </div>

        <div className="form-group">
          <label>Faculty</label>
          <select
            name="faculty"
            value={formData.faculty}
            onChange={handleChange}
            className="form-control"
            required
          >
            <option value="FICT">FICT - Faculty of Information Communication Technology</option>
            <option value="FBMG">FBMG - Faculty of Business Management</option>
            <option value="FABE">FABE - Faculty of Architecture and Built Environment</option>
          </select>
        </div>

        {(formData.role === 'student' || formData.role === 'lecturer') && (
          <div className="form-group">
            <label>
              {formData.role === 'student' ? 'Select Your Class' : 'Select Assigned Class (Optional)'}
            </label>
            <select
              name="class_id"
              value={formData.class_id}
              onChange={handleChange}
              className="form-control"
              required={formData.role === 'student'}
            >
              <option value="">Select Class</option>
              {filteredClasses.map(cls => (
                <option key={cls.id} value={cls.id}>
                  {cls.name} ({cls.faculty})
                </option>
              ))}
            </select>
            <small style={{ color: '#cccccc', marginTop: '0.5rem', display: 'block' }}>
              {filteredClasses.length} classes available for {formData.faculty}
            </small>
          </div>
        )}

        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={loading || (formData.role === 'student' && filteredClasses.length === 0)}
        >
          {loading ? 'Registering...' : 'Register'}
        </button>
      </form>

      <div style={{ marginTop: '1rem', textAlign: 'center' }}>
        <p>Already have an account? <a href="/login" style={{ color: '#cccccc' }}>Login here</a></p>
      </div>

      {/* Debug info for development */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{ marginTop: '2rem', padding: '1rem', backgroundColor: '#2c2c2c', borderRadius: '4px' }}>
          <h4>Debug Info:</h4>
          <p>Backend Status: <strong>{backendStatus}</strong></p>
          <p>Classes Loaded: <strong>{classes.length}</strong></p>
          <p>Filtered Classes: <strong>{filteredClasses.length}</strong></p>
          <button 
            onClick={() => connectionAPI.health().then(console.log).catch(console.error)}
            className="btn btn-sm btn-secondary"
          >
            Test Backend
          </button>
        </div>
      )}
    </div>
  );
};

export default Register;
