import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await login(email, password);
      navigate('/');
    } catch (error) {
      setError(error.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Demo accounts for testing
  const demoAccounts = [
    { email: 'lecturer@luct.ac.ls', password: 'password', role: 'Lecturer' },
    { email: 'student@luct.ac.ls', password: 'password', role: 'Student' },
    { email: 'prl@luct.ac.ls', password: 'password', role: 'Principal Lecturer' },
    { email: 'pl@luct.ac.ls', password: 'password', role: 'Program Leader' }
  ];

  const fillDemoAccount = (demoEmail, demoPassword) => {
    setEmail(demoEmail);
    setPassword(demoPassword);
  };

  return (
    <div className="login-container">
      <div className="card">
        <h2>LUCT Reporting System</h2>
        <p style={{ textAlign: 'center', color: '#64748b', marginBottom: '20px' }}>
          Login to access the reporting system
        </p>
        
        {error && <div className="alert alert-error">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        {/* Demo Accounts */}
        <div style={{ marginTop: '30px', padding: '20px', background: 'rgba(167, 139, 250, 0.1)', borderRadius: '12px' }}>
          <h4 style={{ marginBottom: '15px', color: '#7e22ce' }}>Demo Accounts:</h4>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            {demoAccounts.map((account, index) => (
              <button
                key={index}
                type="button"
                className="btn btn-secondary btn-sm"
                onClick={() => fillDemoAccount(account.email, account.password)}
                style={{ fontSize: '0.8rem' }}
              >
                Login as {account.role}
              </button>
            ))}
          </div>
          <p style={{ fontSize: '0.8rem', color: '#64748b', marginTop: '10px', textAlign: 'center' }}>
            Password for all demo accounts: <strong>password</strong>
          </p>
        </div>

        <p style={{ marginTop: '20px', textAlign: 'center', color: '#64748b' }}>
          Don't have an account? <Link to="/register" style={{ color: '#a78bfa', fontWeight: '600' }}>Register here</Link>
        </p>
        <p style={{ marginTop: '20px', textAlign: 'center', color: '#94a3b8' }}>
  <Link to="/public" style={{ color: '#cbd5e1', textDecoration: 'none' }}>
    ‚Üê Back to Home
  </Link>
</p>
      </div>
    </div>
  );
};

export default Login;
