import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Navigation from '../components/Navigation';
import ReportForm from '../components/ReportForm';
import SignaturePad from '../components/SignaturePad';

const Reports = () => {
  const { user } = useAuth();
  const [reports, setReports] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [showSignature, setShowSignature] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchReports();
  }, [user]);

  const fetchReports = async () => {
    try {
      setLoading(true);
      const endpoint = user?.role === 'lecturer' ? '/reports/my-reports' : '/reports/all';
      const response = await api.get(endpoint);
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
      alert('Error loading reports');
    } finally {
      setLoading(false);
    }
  };

  const handleReportCreated = (newReport) => {
    setReports(prev => [newReport, ...prev]);
    setShowForm(false);
    alert('Report created successfully!');
  };

  const handleApproveReport = async (reportId) => {
    setSelectedReport(reportId);
    setShowSignature(true);
  };

  const handleSignatureSave = async (signature) => {
    try {
      await api.patch(`/reports/${selectedReport}/approve`, { signature });
      await fetchReports();
      setShowSignature(false);
      setSelectedReport(null);
      alert('Report approved successfully!');
    } catch (error) {
      console.error('Error approving report:', error);
      alert('Error approving report');
    }
  };

  const addPRLFeedback = async (reportId) => {
    const feedback = prompt('Enter your feedback for this report:');
    if (feedback && feedback.trim()) {
      try {
        await api.patch(`/reports/${reportId}/feedback`, { feedback });
        await fetchReports();
        alert('Feedback added successfully!');
      } catch (error) {
        console.error('Error adding feedback:', error);
        alert('Error adding feedback');
      }
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { color: '#ffc107', text: 'PENDING' },
      student_approved: { color: '#17a2b8', text: 'STUDENT APPROVED' },
      prl_approved: { color: '#28a745', text: 'PRL APPROVED' },
      rejected: { color: '#dc3545', text: 'REJECTED' }
    };

    const config = statusConfig[status] || statusConfig.pending;
    
    return (
      <span style={{
        padding: '4px 8px',
        borderRadius: '4px',
        fontSize: '0.8em',
        backgroundColor: config.color,
        color: 'white',
        fontWeight: 'bold'
      }}>
        {config.text}
      </span>
    );
  };

  const canCreateReport = ['lecturer', 'prl', 'pl', 'fmg'].includes(user?.role);

  return (
    <div>
      <Navigation />
      <div className="container">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h1>Reports Management</h1>
          {canCreateReport && (
            <button 
              className="btn btn-primary" 
              onClick={() => setShowForm(!showForm)}
            >
              {showForm ? 'Cancel' : 'Create New Report'}
            </button>
          )}
        </div>

        {showForm && <ReportForm onReportCreated={handleReportCreated} />}

        {showSignature && (
          <div className="card">
            <h3>Approve Report - Please Sign</h3>
            <p>Use your mouse to sign below confirming that the reported topics were taught and attendance is accurate:</p>
            <SignaturePad onSave={handleSignatureSave} />
            <button 
              className="btn btn-secondary" 
              onClick={() => setShowSignature(false)}
              style={{ marginTop: '10px' }}
            >
              Cancel Approval
            </button>
          </div>
        )}

        <div className="card">
          <h3>{user?.role === 'lecturer' ? 'My Reports' : 'All Reports'}</h3>
          
          {loading ? (
            <p>Loading reports...</p>
          ) : reports.length === 0 ? (
            <p>No reports found.</p>
          ) : (
            <div style={{ overflowX: 'auto' }}>
              <table className="table">
                <thead>
                  <tr>
                    <th>Course Code</th>
                    <th>Course Name</th>
                    <th>Class</th>
                    <th>Week</th>
                    <th>Date</th>
                    <th>Students</th>
                    <th>Lecturer</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {reports.map(report => (
                    <tr key={report.id}>
                      <td><strong>{report.course_code}</strong></td>
                      <td>{report.course_name}</td>
                      <td>{report.class_name}</td>
                      <td>{report.week_of_reporting}</td>
                      <td>{new Date(report.date_of_lecture).toLocaleDateString()}</td>
                      <td>{report.students_present}/{report.total_students}</td>
                      <td>{report.lecturer_name}</td>
                      <td>{getStatusBadge(report.status)}</td>
                      <td>
                        <div style={{ display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                          {/* Student actions */}
                          {user?.role === 'student' && report.status === 'pending' && (
                            <button 
                              className="btn btn-success btn-sm"
                              onClick={() => handleApproveReport(report.id)}
                              style={{ fontSize: '0.8em', padding: '5px 10px' }}
                            >
                              Approve & Sign
                            </button>
                          )}
                          
                          {/* PRL actions */}
                          {user?.role === 'prl' && report.status === 'student_approved' && !report.prl_feedback && (
                            <button 
                              className="btn btn-primary btn-sm"
                              onClick={() => addPRLFeedback(report.id)}
                              style={{ fontSize: '0.8em', padding: '5px 10px' }}
                            >
                              Add Feedback
                            </button>
                          )}

                          {/* View details button */}
                          <button 
                            className="btn btn-secondary btn-sm"
                            onClick={() => {
                              alert(`Report Details:\n\nTopic: ${report.topic_taught}\nLearning Outcomes: ${report.learning_outcomes}\nVenue: ${report.venue}\nTime: ${report.scheduled_time}\n${report.prl_feedback ? `PRL Feedback: ${report.prl_feedback}` : ''}`);
                            }}
                            style={{ fontSize: '0.8em', padding: '5px 10px' }}
                          >
                            View Details
                          </button>
