import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Navigation from '../components/Navigation';
import { exportRatingsToExcel } from '../utils/exportToExcel';

const Rating = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [newRating, setNewRating] = useState({
    lecturer: '',
    course: '',
    rating: 0,
    comment: '',
    category: 'teaching'
  });
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      
      const [ratingsResponse, coursesResponse, lecturersResponse] = await Promise.all([
        api.get('/ratings'),
        api.get('/courses'),
        api.get('/users/lecturers')
      ]);
      
      setRatings(ratingsResponse.data || []);
      setCourses(coursesResponse.data || []);
      setLecturers(lecturersResponse.data || []);
    } catch (error) {
      console.error('Error fetching initial data:', error);
      setError('Failed to load data');
      setRatings([]);
      setCourses([]);
      setLecturers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleRatingChange = (e) => {
    const { name, value } = e.target;
    setNewRating(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleStarClick = (ratingValue) => {
    setNewRating(prev => ({
      ...prev,
      rating: ratingValue
    }));
  };

  const submitRating = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const response = await api.post('/ratings', {
        ...newRating,
        user_id: user?.id
      });
      
      setRatings(prev => [response.data.rating, ...prev]);
      setNewRating({
        lecturer: '',
        course: '',
        rating: 0,
        comment: '',
        category: 'teaching'
      });
    } catch (error) {
      console.error('Error submitting rating:', error);
      setError('Failed to submit rating');
    } finally {
      setSubmitting(false);
    }
  };

  const handleExportExcel = () => {
    exportRatingsToExcel(ratings);
  };

  const getAverageRating = () => {
    if (ratings.length === 0) return 0;
    const total = ratings.reduce((acc, curr) => acc + (curr.rating || 0), 0);
    return (total / ratings.length).toFixed(1);
  };

  const renderStars = (rating) => {
    const numericRating = rating || 0;
    return Array.from({ length: 5 }, (_, index) => (
      <span
        key={index}
        className={`star ${index < numericRating ? 'active' : ''}`}
      >
        {index < numericRating ? 'â˜…' : 'â˜†'}
      </span>
    ));
  };

  if (loading) {
    return (
      <div>
        <Navigation />
        <div className="container">
          <div className="login-container">Loading ratings...</div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Navigation />
      <div className="container">
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <div className="rating-header">
              <h1>Course & Lecturer Ratings</h1>
              <p>Share your feedback about courses and lecturers</p>
            </div>
            {ratings.length > 0 && (
              <div className="export-buttons">
                <button onClick={handleExportExcel} className="btn btn-success">
                  ðŸ“Š Export to Excel
                </button>
              </div>
            )}
          </div>

          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}

          {/* Rating Summary */}
          <div className="rating-summary">
            <div className="summary-card">
              <h3>Overall Rating</h3>
              <div className="overall-rating">
                <span className="rating-value">{getAverageRating()}</span>
                <div className="rating-stars">
                  {renderStars(Math.round(parseFloat(getAverageRating())))}
                </div>
                <span className="rating-count">({ratings.length} ratings)</span>
              </div>
            </div>
          </div>

          <div className="rating-content">
            {/* Add Rating Form */}
            <div className="card">
              <h2>Add Your Rating</h2>
              <form onSubmit={submitRating} className="rating-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>Lecturer *</label>
                    <select
                      name="lecturer"
                      value={newRating.lecturer}
                      onChange={handleRatingChange}
                      required
                    >
                      <option value="">Select Lecturer</option>
                      {lecturers.map(lecturer => (
                        <option key={lecturer.id} value={lecturer.name}>
                          {lecturer.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Course *</label>
                    <select
                      name="course"
                      value={newRating.course}
                      onChange={handleRatingChange}
                      required
                    >
                      <option value="">Select Course</option>
                      {courses.map(course => (
                        <option key={course.id} value={course.course_name}>
                          {course.course_name} ({course.course_code})
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="form-group">
                  <label>Category</label>
                  <select
                    name="category"
                    value={newRating.category}
                    onChange={handleRatingChange}
                  >
                    <option value="teaching">Teaching Quality</option>
                    <option value="materials">Course Materials</option>
                    <option value="communication">Communication</option>
                    <option value="overall">Overall Experience</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Rating *</label>
                  <div className="star-rating-input">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <span
                        key={star}
                        className={`star ${star <= newRating.rating ? 'active' : ''}`}
                        onClick={() => handleStarClick(star)}
                      >
                        {star <= newRating.rating ? 'â˜…' : 'â˜†'}
                      </span>
                    ))}
                    <span className="rating-value-display">
                      {newRating.rating > 0 ? `${newRating.rating}/5` : 'Select rating'}
                    </span>
                  </div>
                </div>

                <div className="form-group">
                  <label>Comment</label>
                  <textarea
                    name="comment"
                    value={newRating.comment}
                    onChange={handleRatingChange}
                    rows="4"
                    placeholder="Share your experience and suggestions..."
                  />
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={submitting || !newRating.lecturer || !newRating.course || newRating.rating === 0}
                >
                  {submitting ? 'Submitting...' : 'Submit Rating'}
                </button>
              </form>
            </div>

            {/* Ratings List */}
            <div className="card">
              <h2>Recent Ratings</h2>
              <div className="ratings-list">
                {ratings.map((rating) => (
                  <div key={rating.id} className="rating-item">
                    <div className="rating-header">
                      <div className="rating-info">
                        <h4>{rating.lecturer || 'N/A'}</h4>
                        <span className="course">
                          {rating.course || 'N/A'} ({rating.courseCode || 'No code'})
                        </span>
                      </div>
                      <div className="rating-meta">
                        <div className="rating-stars">{renderStars(rating.rating)}</div>
                        <span className="rating-value">{rating.rating || 0}/5</span>
                      </div>
                    </div>
                    <p className="rating-comment">{rating.comment || 'No comment provided'}</p>
                    <div className="rating-footer">
                      <span className="student">{rating.studentName || rating.user?.name || 'Anonymous'}</span>
                      <span className="category">{rating.category || 'N/A'}</span>
                      <span className="date">
                        {rating.date || (rating.created_at ? new Date(rating.created_at).toLocaleDateString() : 'N/A')}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
              {ratings.length === 0 && !error && (
                <div className="no-ratings">
                  No ratings yet. Be the first to rate!
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Rating;
