import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('ðŸ”— API Base URL:', API_BASE_URL);

// Create axios instance with better defaults
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor with enhanced logging
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    console.log(`ðŸ”„ ${config.method?.toUpperCase()} ${config.url}`, {
      data: config.data,
      params: config.params
    });
    
    return config;
  },
  (error) => {
    console.error('âŒ Request Error:', error);
    return Promise.reject(error);
  }
);

// Enhanced response interceptor
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… ${response.config.method?.toUpperCase()} ${response.config.url}`, {
      status: response.status,
      data: response.data
    });
    return response;
  },
  (error) => {
    const errorDetails = {
      url: error.config?.url,
      method: error.config?.method?.toUpperCase(),
      status: error.response?.status,
      statusText: error.response?.statusText,
      message: error.message,
      code: error.code,
      data: error.response?.data
    };

    console.error('âŒ API Error:', errorDetails);

    // Handle specific error cases
    if (error.code === 'ECONNREFUSED') {
      console.error('ðŸ’¥ Backend server is not running!');
      // Show user-friendly error
      if (typeof window !== 'undefined') {
        alert('Backend server is not running. Please ensure the backend is started on port 5000.');
      }
    }

    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }

    if (error.response?.status === 500) {
      console.error('ðŸš¨ Server error:', error.response.data);
    }

    return Promise.reject({
      ...error,
      userMessage: getErrorMessage(error),
      timestamp: new Date().toISOString()
    });
  }
);

// Enhanced error message helper
const getErrorMessage = (error) => {
  if (error.code === 'ECONNREFUSED') {
    return 'Cannot connect to server. Please check if the backend is running.';
  }
  if (error.response?.data?.error) {
    return error.response.data.error;
  }
  if (error.response?.data?.message) {
    return error.response.data.message;
  }
  if (error.message) {
    return error.message;
  }
  return 'An unexpected error occurred. Please try again.';
};

// Enhanced API methods with retry logic
const createApiWithRetry = (apiCall, maxRetries = 2) => {
  return async (...args) => {
    let lastError;
    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        const result = await apiCall(...args);
        return result;
      } catch (error) {
        lastError = error;
        console.log(`Retry attempt ${attempt} failed:`, error.message);
        if (attempt === maxRetries) break;
        await new Promise(resolve => setTimeout(resolve, 1000 * attempt));
      }
    }
    throw lastError;
  };
};

export const authAPI = {
  login: createApiWithRetry((credentials) => api.post('/auth/login', credentials)),
  register: createApiWithRetry((userData) => api.post('/auth/register', userData)),
  getInitData: createApiWithRetry(() => api.get('/auth/init-data')),
};

export const reportsAPI = {
  create: createApiWithRetry((reportData) => api.post('/reports', reportData)),
  getMyReports: createApiWithRetry(() => api.get('/reports/my-reports')),
  getUnsignedReports: createApiWithRetry(() => api.get('/reports/unsigned')),
  signReport: createApiWithRetry((reportId) => api.put(`/reports/${reportId}/sign`)),
  getAll: createApiWithRetry(() => api.get('/reports')),
  getById: createApiWithRetry((id) => api.get(`/reports/${id}`)),
};

export const usersAPI = {
  getLecturers: createApiWithRetry(() => api.get('/users/lecturers')),
  getStudents: createApiWithRetry(() => api.get('/users/students')),
  getClasses: createApiWithRetry(() => api.get('/users/classes')),
  getCourses: createApiWithRetry(() => api.get('/users/courses')),
  assignClass: createApiWithRetry((assignmentData) => api.post('/users/assign-class', assignmentData)),
  getUserStats: createApiWithRetry((userId) => api.get(`/users/${userId}/stats`)),
};

export const complaintsAPI = {
  create: createApiWithRetry((complaintData) => api.post('/complaints', complaintData)),
  getMyComplaints: createApiWithRetry(() => api.get('/complaints/my-complaints')),
  getComplaintsForMe: createApiWithRetry(() => api.get('/complaints/for-me')),
  respond: createApiWithRetry((complaintId, response) => api.put(`/complaints/${complaintId}/respond`, { response })),
  getAll: createApiWithRetry(() => api.get('/complaints')),
};

// Enhanced connection testing
export const connectionAPI = {
  health: () => api.get('/health'),
  testAllEndpoints: async () => {
    const results = {};
    try {
      results.health = await api.get('/health');
      results.auth = await api.get('/auth/init-data').catch(() => null);
      results.classes = await api.get('/users/classes').catch(() => null);
      results.reports = await api.get('/reports').catch(() => null);
      return results;
    } catch (error) {
      console.error('Comprehensive connection test failed:', error);
      return results;
    }
  },
};

// Export enhanced api instance
export default api;
