import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import './Rating.css';

const Rating = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [newRating, setNewRating] = useState({
    lecturer: '',
    course: '',
    rating: 0,
    comment: '',
    category: 'teaching' // teaching, materials, communication, overall
  });
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);

  // Mock data - replace with actual API calls
  useEffect(() => {
    const mockRatings = [
      {
        id: 1,
        studentName: 'John Doe',
        lecturer: 'Dr. Smith',
        course: 'Web Development',
        courseCode: 'DIWA2110',
        rating: 4.5,
        comment: 'Excellent teaching methodology and very helpful.',
        category: 'teaching',
        date: '2024-03-15'
      },
      {
        id: 2,
        studentName: 'Jane Smith',
        lecturer: 'Dr. Johnson',
        course: 'Database Systems',
        courseCode: 'DIDB2110',
        rating: 3.8,
        comment: 'Good course content but could use more practical examples.',
        category: 'materials',
        date: '2024-03-14'
      }
    ];

    setRatings(mockRatings);
    setLoading(false);
  }, []);

  const handleRatingChange = (e) => {
    const { name, value } = e.target;
    setNewRating(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleStarClick = (ratingValue) => {
    setNewRating(prev => ({
      ...prev,
      rating: ratingValue
    }));
  };

  const submitRating = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      const ratingToAdd = {
        id: ratings.length + 1,
        studentName: 'Current User', // Replace with actual user name
        lecturer: newRating.lecturer,
        course: newRating.course,
        courseCode: 'TBD',
        rating: parseFloat(newRating.rating),
        comment: newRating.comment,
        category: newRating.category,
        date: new Date().toISOString().split('T')[0]
      };

      setRatings(prev => [ratingToAdd, ...prev]);
      setNewRating({
        lecturer: '',
        course: '',
        rating: 0,
        comment: '',
        category: 'teaching'
      });
      setSubmitting(false);
    }, 1000);
  };

  const getAverageRating = () => {
    if (ratings.length === 0) return 0;
    const total = ratings.reduce((acc, curr) => acc + curr.rating, 0);
    return (total / ratings.length).toFixed(1);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span
        key={index}
        className={`star ${index < rating ? 'filled' : ''}`}
      >
        {index < rating ? '★' : '☆'}
      </span>
    ));
  };

  if (loading) {
    return <div className="rating-container">Loading ratings...</div>;
  }

  return (
    <div className="rating-container">
      <div className="rating-header">
        <h1>Course & Lecturer Ratings</h1>
        <p>Share your feedback about courses and lecturers</p>
      </div>

      {/* Rating Summary */}
      <div className="rating-summary">
        <div className="summary-card">
          <h3>Overall Rating</h3>
          <div className="overall-rating">
            <span className="rating-value">{getAverageRating()}</span>
            <div className="stars">
              {renderStars(Math.round(parseFloat(getAverageRating())))}
            </div>
            <span className="rating-count">({ratings.length} ratings)</span>
          </div>
        </div>
      </div>

      <div className="rating-content">
        {/* Add Rating Form */}
        <div className="add-rating-section">
          <h2>Add Your Rating</h2>
          <form onSubmit={submitRating} className="rating-form">
            <div className="form-row">
              <div className="form-group">
                <label>Lecturer *</label>
                <input
                  type="text"
                  name="lecturer"
                  value={newRating.lecturer}
                  onChange={handleRatingChange}
                  required
                  placeholder="Enter lecturer's name"
                />
              </div>
              <div className="form-group">
                <label>Course *</label>
                <input
                  type="text"
                  name="course"
                  value={newRating.course}
                  onChange={handleRatingChange}
                  required
                  placeholder="Enter course name"
                />
              </div>
            </div>

            <div className="form-group">
              <label>Category</label>
              <select
                name="category"
                value={newRating.category}
                onChange={handleRatingChange}
              >
                <option value="teaching">Teaching Quality</option>
                <option value="materials">Course Materials</option>
                <option value="communication">Communication</option>
                <option value="overall">Overall Experience</option>
              </select>
            </div>

            <div className="form-group">
              <label>Rating *</label>
              <div className="star-rating-input">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span
                    key={star}
                    className={`star ${star <= newRating.rating ? 'filled' : ''}`}
                    onClick={() => handleStarClick(star)}
                  >
                    {star <= newRating.rating ? '★' : '☆'}
                  </span>
                ))}
                <span className="rating-value-display">
                  {newRating.rating > 0 ? `${newRating.rating}/5` : 'Select rating'}
                </span>
              </div>
            </div>

            <div className="form-group">
              <label>Comment</label>
              <textarea
                name="comment"
                value={newRating.comment}
                onChange={handleRatingChange}
                rows="4"
                placeholder="Share your experience and suggestions..."
              />
            </div>

            <button 
              type="submit" 
              className="btn-primary"
              disabled={submitting || !newRating.lecturer || !newRating.course || newRating.rating === 0}
            >
              {submitting ? 'Submitting...' : 'Submit Rating'}
            </button>
          </form>
        </div>

        {/* Ratings List */}
        <div className="ratings-list-section">
          <h2>Recent Ratings</h2>
          <div className="ratings-list">
            {ratings.map((rating) => (
              <div key={rating.id} className="rating-item">
                <div className="rating-header">
                  <div className="rating-info">
                    <h4>{rating.lecturer}</h4>
                    <span className="course">{rating.course} ({rating.courseCode})</span>
                  </div>
                  <div className="rating-meta">
                    <div className="stars">{renderStars(rating.rating)}</div>
                    <span className="rating-value">{rating.rating}/5</span>
                  </div>
                </div>
                <p className="rating-comment">{rating.comment}</p>
                <div className="rating-footer">
                  <span className="student">{rating.studentName}</span>
                  <span className="category">{rating.category}</span>
                  <span className="date">{rating.date}</span>
                </div>
              </div>
            ))}
          </div>
          {ratings.length === 0 && (
            <div className="no-ratings">
              No ratings yet. Be the first to rate!
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Rating;
