import React, { useState } from 'react';
import { reportsAPI } from '../api';
import { useNavigate } from 'react-router-dom';

const ReportForm = ({ user }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    faculty_name: user.faculty,
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: user.name,
    actual_students_present: '',
    total_registered_students: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      await reportsAPI.create(formData);
      setSuccess('Report created successfully! It will be sent to students for signing.');
      setTimeout(() => {
        navigate('/reports');
      }, 2000);
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to create report. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2>Create Lecture Report</h2>
      {error && <div className="alert alert-error">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}
      
      <form onSubmit={handleSubmit}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
          <div className="form-group">
            <label>Faculty Name</label>
            <input
              type="text"
              name="faculty_name"
              value={formData.faculty_name}
              onChange={handleChange}
              className="form-control"
              required
              readOnly
            />
          </div>

          <div className="form-group">
            <label>Class Name</label>
            <input
              type="text"
              name="class_name"
              value={formData.class_name}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="e.g., BIT-1A"
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
          <div className="form-group">
            <label>Week of Reporting</label>
            <input
              type="text"
              name="week_of_reporting"
              value={formData.week_of_reporting}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="e.g., Week 6"
            />
          </div>

          <div className="form-group">
            <label>Date of Lecture</label>
            <input
              type="date"
              name="date_of_lecture"
              value={formData.date_of_lecture}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
          <div className="form-group">
            <label>Course Name</label>
            <input
              type="text"
              name="course_name"
              value={formData.course_name}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="e.g., Web Development"
            />
          </div>

          <div className="form-group">
            <label>Course Code</label>
            <input
              type="text"
              name="course_code"
              value={formData.course_code}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="e.g., WD101"
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
          <div className="form-group">
            <label>Lecturer's Name</label>
            <input
              type="text"
              name="lecturer_name"
              value={formData.lecturer_name}
              onChange={handleChange}
              className="form-control"
              required
              readOnly
            />
          </div>

          <div className="form-group">
            <label>Venue</label>
            <input
              type="text"
              name="venue"
              value={formData.venue}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="e.g., Room 101"
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
          <div className="form-group">
            <label>Actual Students Present</label>
            <input
              type="number"
              name="actual_students_present"
              value={formData.actual_students_present}
              onChange={handleChange}
              className="form-control"
              required
              min="1"
              placeholder="Number of students present"
            />
          </div>

          <div className="form-group">
            <label>Total Registered Students</label>
            <input
              type="number"
              name="total_registered_students"
              value={formData.total_registered_students}
              onChange={handleChange}
              className="form-control"
              required
              min="1"
              placeholder="Total class size"
            />
          </div>
        </div>

        <div className="form-group">
          <label>Scheduled Lecture Time</label>
          <input
            type="time"
            name="scheduled_time"
            value={formData.scheduled_time}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <div className="form-group">
          <label>Topic Taught</label>
          <textarea
            name="topic_taught"
            value={formData.topic_taught}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Describe the topics covered in this lecture..."
          />
        </div>

        <div className="form-group">
          <label>Learning Outcomes</label>
          <textarea
            name="learning_outcomes"
            value={formData.learning_outcomes}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="What should students be able to do after this lecture?"
          />
        </div>

        <div className="form-group">
          <label>Lecturer's Recommendations</label>
          <textarea
            name="recommendations"
            value={formData.recommendations}
            onChange={handleChange}
            className="form-control"
            required
            placeholder="Any recommendations for improvement or follow-up..."
          />
        </div>

        <div className="action-buttons">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? 'Creating Report...' : 'Create Report'}
          </button>
          <button 
            type="button" 
            className="btn btn-danger"
            onClick={() => navigate('/dashboard')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ReportForm;
